import React from 'react';

const Button = props => {
  const [input, setInput] = useState('');
  return (
    <button
      value={props.value}
      style={props.itemType === 'number' ? styles.number : styles.operator}
      onClick={() => setInput()}
    >
      {props.item}
    </button>
  );
};

const styles = {
  number: {
    width: '20%',
    margin: '0.2vw',
    height: '10vh',
    background: '#D6D6FA',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    borderRadius: '0.5rem',
    fontWeight: 'bold',
    fontSize: 16
  },

  operator: {
    width: '20%',
    margin: '0.2vw',
    height: '10vh',
    background: '#6D6D7F',
    color: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    borderRadius: '0.5rem',
    fontWeight: 'bold',
    fontSize: 16
  }
};

export default Button;
